-- Trigger para aumentar el numero de afiliado sin tener que hacerlo manual
DELIMITER $$

CREATE TRIGGER before_insert_affiliate
BEFORE INSERT ON affiliates
FOR EACH ROW
BEGIN
    SET NEW.afilliate_code = (SELECT COALESCE(MAX(afilliate_code), 89999) + 1 FROM affiliates);
END $$

DELIMITER ;

SELECT 
    *,
    DATE_FORMAT(entry_date, '%d-%m-%Y') AS formatted_entry_date
FROM affiliates;

SELECT IF(has_social = 1, 'X', '') AS has_social_formateada
FROM affiliates;
